static int glad_parse_version(const char* version_str, int* major, int* minor) {
    const char* p;
    if (!version_str) return 0;
    /* Skip any leading non-digit characters (e.g. "OpenGL ES") */
    p = version_str;
    while (*p && (*p < '0' || *p > '9')) p++;
    if (!*p) return 0;
    *major = 0;
    while (*p >= '0' && *p <= '9') {
        *major = *major * 10 + (*p - '0');
        p++;
    }
    if (*p == '.') p++;
    *minor = 0;
    while (*p >= '0' && *p <= '9') {
        *minor = *minor * 10 + (*p - '0');
        p++;
    }
    return 1;
}

