    if (!glGetString) return 0;

    version_str = (const char*)glGetString(GL_VERSION);
    if (!glad_parse_version(version_str, &major, &minor)) return 0;

    /* Check that we got at least GL 3.3 */
    if (major < 3 || (major == 3 && minor < 3)) return 0;

    /* Load all function groups up to GL 3.3 */
